@mixin section-title {
    text-transform: uppercase;
    font-size: 30px;
    letter-spacing: 0.05em;

}

body {
    margin: 0;
    padding: 0;
    font-family: 'Vollkorn', serif;
}

.header {
    height: 100px;
    background-color: #2D2D2D;
    display: flex;
    align-items: center;
    justify-content: space-around;

    &__logo {
        margin-left: 30px;
    }

    &__menu {
        list-style-type: none;
        display: flex;
        align-items: center;
        padding: 0;


        &__item  {
            padding-right: 20px;
        }
    }

    &__icon {
        height: 30px;
        width: 30px;
    }
}

.goods {
    height: 1000px;
    display: flex;
    align-items: center;
    flex-direction: column;
    margin-top: 40px;
    &__title {
        @include section-title; 
    }

    &__container {
        display: grid;
        grid-template-columns: repeat(3, 1fr);
        grid-column-gap: 30px;
        grid-row-gap: 30px;
        margin-top: 30px;
        padding-bottom: 50px;


        &__item {   
            display: flex;
            flex-direction: column;
            align-items: center;

            &__img {
                height: 350px;
                width: 300px;
            }

            &__name {
                font-size: 20px;

            }

            &__price {
                font-size: 20px;
                margin-top: -10px;
            }

            &__button {
                text-align: center;
                width: 120px;
                height: 35px;
                border-radius: 45px;
                font-size: 15px;
                color: #524f4e;
                background: white;
                text-transform: uppercase;
                font-family: 'Vollkorn', serif;
                letter-spacing: 0.03em;
                box-shadow: 0 8px 15px rgba(0, 0, 0, .1);
                transition: .3s; 

                &:hover {
                    background: #225013;
                    box-shadow: 0 15px 20px rgba(46, 229, 157, .4);
                    color: white;
                    transform: translateY(-7px);
                }
            }

            
        }
    }
}

.cart {
    display: flex;
    align-items: center;
    flex-direction: column;

    &__title {
        @include section-title; 
        margin-top: 40px;
        margin-bottom: 40px;
    }
    

    &__list {
        width: 500px;
        align-items: center;
        display: flex;
        flex-direction: column;

        &__item {
            display: flex;
            align-items: center;
            width: 100%;
            margin-bottom: 10px;
            padding: 10px;
            border: 1px solid #0000005e;
            position: relative;


            &__img {
                height: 100px;
                width: 70px;
            }

            &__text {
                display: flex;
            }

            &__name {
                margin-right: 10px;
                margin-left: 10px;
                width: 260px;
                text-overflow: ellipsis;
                overflow: hidden;
                white-space: nowrap;
            }

            &__amount-cnt { 
                position: absolute;
                right: 30px;
                display: flex;
            }

            &__amount { 
                font-size: 30px;
                padding: 0;
                margin: 0;
            }

            &__remove {
                width: 15px;
                height: 30px;
                margin: 5px;
                margin-top: 0;
                padding: 0;
                font-size: 30px;
                cursor: pointer;
            }

            &__add {
                width: 15px;
                height: 30px;
                margin: 5px;
                margin-top: 0;
                padding: 0;
                font-size: 30px;
                cursor: pointer;
            }

            &__delete {
                position: absolute;
                top: 2px;
                right: 8px;
                font-size: 22px;
                font-family: monospace;
                cursor: pointer;
            }
        }

        &__total {
            font-size: 20px;
            letter-spacing: 0.02em;
            margin-bottom: 15px;
            display: flex;

            &__text {
                margin: 0;
                margin-left: 5px;
            }
        }

        &__button {
            text-align: center;
                width: 120px;
                height: 35px;
                border-radius: 45px;
                font-size: 15px;
                color: #524f4e;
                background: white;
                text-transform: uppercase;
                font-family: 'Vollkorn', serif;
                letter-spacing: 0.03em;
                box-shadow: 0 8px 15px rgba(0, 0, 0, .1);
                transition: .3s; 

                &:hover {
                    background: #225013;
                    box-shadow: 0 15px 20px rgba(46, 229, 157, .4);
                    color: white;
                    transform: translateY(-7px);
                }
        }
    }
}

.success {
    &__text {
        font-size: 45px;
        width: 600px;
        margin: 100px auto;
        padding: 50px;
        border: 1px solid #0000005e;
    }
}
